mapper.select:
select pa.postIdx, pa.postAssignmentTitle, pa.postAssignmentRegDate, pa.postAssignmentHits, m.memberNickname, a.assignmentEndDate from postAssignment as pa join member as m on pa.memberIdx = m.memberIdx join post as p on pa.postIdx = p.postIdx join assignment as a on a.assignmentIdx = pa.assignmentIdx where p.boardIdx = #{boardIdx}
and pa.postAssignmentTitle like concat('%', #{keyword}, '%')
and pa.postAssignmentContents like concat('%', #{keyword}, '%')
and (pa.postAssignmentTitle like concat('%', #{keyword}, '%') or pa.postAssignmentContents like concat('%', #{keyword}, '%'))
and m.memberNickname like concat('%', #{keyword}, '%')
order by pa.postAssignmentRegDate desc limit #{postsPerPage} offset #{offset}

mapper.update:
update postAssignment
set postAssignmentHits = postAssignmentHits + 1
where postIdx = #{postIdx}
